class JSZip {
  constructor() {
    this.files = new Map();
  }

  file(name, content) {
    this.files.set(name, content);
  }

  async generateAsync(options) {
    const blobs = [];
    for (const [name, content] of this.files.entries()) {
      blobs.push(content);
    }
    return new Blob(blobs, { type: 'application/zip' });
  }
}

window.JSZip = JSZip; 